import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { Box } from '@mui/material';
import { ApolloProvider } from '@apollo/client';

import client from './apollo/client';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import { ThemeProvider } from './contexts/ThemeContext';
import { LanguageProvider } from './contexts/LanguageContext';

// PropTypes se utiliza para validar las props de nuestros componentes de orden superior.
import PropTypes from 'prop-types';

// Componentes
import Layout from './components/Layout/Layout';
import Landing from './pages/Landing/Landing';
import Login from './pages/Auth/Login';
import Register from './pages/Auth/Register';
import Dashboard from './pages/Dashboard/Dashboard';
import Courses from './pages/Courses/Courses';
import CourseDetail from './pages/Courses/CourseDetail';
import Loans from './pages/Loans/Loans';
import Profile from './pages/Profile/Profile';
import LoadingScreen from './components/Common/LoadingScreen';

// Admin
import ManageCourses from './pages/Admin/ManageCourses';
import CreateCourse from './pages/Admin/CreateCourse';
import EditCourse from './pages/Admin/EditCourse';

// Componente de rutas protegidas
const ProtectedRoute = ({ children }) => {
  const { isAuthenticated, loading } = useAuth();
  
  if (loading) {
    return <LoadingScreen />;
  }
  
  return isAuthenticated ? children : <Navigate to="/login" replace />;
};

// Componente de rutas p煤blicas (solo para usuarios no autenticados)
const PublicRoute = ({ children }) => {
  const { isAuthenticated, loading } = useAuth();
  
  if (loading) {
    return <LoadingScreen />;
  }
  
  return !isAuthenticated ? children : <Navigate to="/app/dashboard" replace />;
};

// Validaci贸n de props para componentes de rutas. Se espera que children sea un nodo v谩lido.
ProtectedRoute.propTypes = {
  children: PropTypes.node.isRequired,
};

PublicRoute.propTypes = {
  children: PropTypes.node.isRequired,
};



// Ruta protegida para administradores
const AdminRoute = ({ children }) => {
  const { user, loading } = useAuth();
  if (loading) return <LoadingScreen />;
  if (!user) return <Navigate to="/login" replace />;
  return user.role === 'admin' ? children : <Navigate to="/app/dashboard" replace />;
};

AdminRoute.propTypes = {
  children: PropTypes.node.isRequired,
};
function App() {
  return (
    <ApolloProvider client={client}>
      <AuthProvider>
        <ThemeProvider>
          <LanguageProvider>
            <Router>
              <Box sx={{ minHeight: '100vh', backgroundColor: 'background.default' }}>
                <Routes>
                {/* P谩gina de inicio */}
                <Route 
                  path="/" 
                  element={
                    <PublicRoute>
                      <Landing />
                    </PublicRoute>
                  } 
                />
                
                {/* Rutas p煤blicas */}
                <Route 
                  path="/login" 
                  element={
                    <PublicRoute>
                      <Login />
                    </PublicRoute>
                  } 
                />
                <Route 
                  path="/register" 
                  element={
                    <PublicRoute>
                      <Register />
                    </PublicRoute>
                  } 
                />
                
                {/* Rutas protegidas */}
                <Route 
                  path="/app" 
                  element={
                    <ProtectedRoute>
                      <Layout />
                    </ProtectedRoute>
                  }
                >
                  <Route index element={<Navigate to="/app/dashboard" replace />} />
                  <Route path="dashboard" element={<Dashboard />} />
                  <Route path="courses" element={<Courses />} />
                  <Route path="courses/:id" element={<CourseDetail />} />
                  <Route path="loans" element={<Loans />} />
                  <Route path="profile" element={<Profile />} />
                  
                  {/* Rutas de Admin */}
                  <Route path="admin/courses" element={<AdminRoute><ManageCourses /></AdminRoute>} />
                  <Route path="admin/courses/new" element={<AdminRoute><CreateCourse /></AdminRoute>} />
                  <Route path="admin/courses/edit/:id" element={<AdminRoute><EditCourse /></AdminRoute>} />
                </Route>
                
                {/* Ruta por defecto */}
                <Route path="*" element={<Navigate to="/" replace />} />
              </Routes>
            </Box>
          </Router>
          </LanguageProvider>
        </ThemeProvider>
      </AuthProvider>
    </ApolloProvider>
  );
}

export default App;

import React, { useRef, useState } from 'react';
import PropTypes from 'prop-types';
import { Button } from '@mui/material';

const AnimatedButton = ({ 
  children, 
  onClick, 
  variant = 'contained',
  color = 'primary',
  size = 'medium',
  startIcon,
  endIcon,
  fullWidth = false,
  disabled = false,
  animationType = 'ripple',
  ...props 
}) => {
  const buttonRef = useRef(null);
  const [isAnimating, setIsAnimating] = useState(false);

  const handleClick = (e) => {
    if (disabled || isAnimating) return;

    setIsAnimating(true);

    // Importar anime din谩micamente
    import('animejs').then((animeModule) => {
      // Intentar obtener la funci贸n anime de diferentes formas
      let animeFunc = animeModule.default;
      if (!animeFunc && typeof animeModule === 'function') {
        animeFunc = animeModule;
      }
      if (!animeFunc && animeModule.anime) {
        animeFunc = animeModule.anime;
      }

      // Si a煤n no tenemos la funci贸n, buscar en las propiedades del m贸dulo
      if (!animeFunc) {
        const keys = Object.keys(animeModule);
        for (const key of keys) {
          if (typeof animeModule[key] === 'function') {
            animeFunc = animeModule[key];
            break;
          }
        }
      }

      if (!animeFunc) {
        console.error('No se pudo cargar anime.js', animeModule);
        setIsAnimating(false);
        if (onClick) onClick(e);
        return;
      }

      if (animationType === 'ripple') {
        // Efecto ripple personalizado
        const rect = buttonRef.current.getBoundingClientRect();
        const x = e.clientX - rect.left;
        const y = e.clientY - rect.top;

        const ripple = document.createElement('span');
        ripple.style.position = 'absolute';
        ripple.style.left = `${x}px`;
        ripple.style.top = `${y}px`;
        ripple.style.width = '0px';
        ripple.style.height = '0px';
        ripple.style.borderRadius = '50%';
        ripple.style.backgroundColor = 'rgba(255, 255, 255, 0.6)';
        ripple.style.transform = 'translate(-50%, -50%)';
        ripple.style.pointerEvents = 'none';

        buttonRef.current.appendChild(ripple);

        animeFunc({
          targets: ripple,
          width: rect.width * 2,
          height: rect.width * 2,
          opacity: [0.6, 0],
          duration: 600,
          easing: 'easeOutExpo',
          complete: () => {
            ripple.remove();
            setIsAnimating(false);
          }
        });
      } else if (animationType === 'pulse') {
        // Efecto de pulso
        animeFunc({
          targets: buttonRef.current,
          scale: [1, 1.1, 1],
          duration: 300,
          easing: 'easeInOutQuad',
          complete: () => {
            setIsAnimating(false);
          }
        });
      } else if (animationType === 'shake') {
        // Efecto de sacudida
        animeFunc({
          targets: buttonRef.current,
          translateX: [0, -10, 10, -10, 10, 0],
          duration: 400,
          easing: 'easeInOutQuad',
          complete: () => {
            setIsAnimating(false);
          }
        });
      } else if (animationType === 'bounce') {
        // Efecto de rebote
        animeFunc({
          targets: buttonRef.current,
          translateY: [0, -15, 0],
          duration: 500,
          easing: 'easeOutBounce',
          complete: () => {
            setIsAnimating(false);
          }
        });
      } else if (animationType === 'rotate') {
        // Efecto de rotaci贸n
        animeFunc({
          targets: buttonRef.current,
          rotate: [0, 360],
          duration: 600,
          easing: 'easeInOutQuad',
          complete: () => {
            setIsAnimating(false);
          }
        });
      }

      // Ejecutar el onClick despu茅s de un peque帽o delay
      setTimeout(() => {
        if (onClick) onClick(e);
      }, 100);
    });
  };

  return (
    <Button
      ref={buttonRef}
      variant={variant}
      color={color}
      size={size}
      startIcon={startIcon}
      endIcon={endIcon}
      fullWidth={fullWidth}
      disabled={disabled}
      onClick={handleClick}
      sx={{
        position: 'relative',
        overflow: 'hidden',
        transition: 'all 0.3s ease',
        '&:hover': {
          transform: 'translateY(-2px)',
          boxShadow: '0 4px 12px rgba(0,0,0,0.15)',
        },
        ...props.sx
      }}
      {...props}
    >
      {children}
    </Button>
  );
};

AnimatedButton.propTypes = {
  children: PropTypes.node.isRequired,
  onClick: PropTypes.func,
  variant: PropTypes.oneOf(['contained', 'outlined', 'text']),
  color: PropTypes.oneOf(['primary', 'secondary', 'success', 'error', 'warning', 'info']),
  size: PropTypes.oneOf(['small', 'medium', 'large']),
  startIcon: PropTypes.node,
  endIcon: PropTypes.node,
  fullWidth: PropTypes.bool,
  disabled: PropTypes.bool,
  animationType: PropTypes.oneOf(['ripple', 'pulse', 'shake', 'bounce', 'rotate']),
  sx: PropTypes.object,
};

export default AnimatedButton;

import React, { useEffect, useRef } from 'react';
import PropTypes from 'prop-types';

const AnimatedCounter = ({ value, duration = 2000, delay = 0, prefix = '', suffix = '' }) => {
  const counterRef = useRef(null);
  const animationRef = useRef(null);

  useEffect(() => {
    if (!counterRef.current) return;

    // Importar anime din谩micamente
    import('animejs').then((animeModule) => {
      // Intentar obtener la funci贸n anime de diferentes formas
      let animeFunc = animeModule.default;
      if (!animeFunc && typeof animeModule === 'function') {
        animeFunc = animeModule;
      }
      if (!animeFunc && animeModule.anime) {
        animeFunc = animeModule.anime;
      }

      // Si a煤n no tenemos la funci贸n, buscar en las propiedades del m贸dulo
      if (!animeFunc) {
        const keys = Object.keys(animeModule);
        for (const key of keys) {
          if (typeof animeModule[key] === 'function') {
            animeFunc = animeModule[key];
            break;
          }
        }
      }

      if (!animeFunc) {
        console.error('No se pudo cargar anime.js', animeModule);
        return;
      }

      // Objeto para animar
      const obj = { count: 0 };

      // Crear nueva animaci贸n
      animationRef.current = animeFunc({
        targets: obj,
        count: value,
        duration: duration,
        delay: delay,
        easing: 'easeOutExpo',
        round: 1,
        update: function() {
          if (counterRef.current) {
            counterRef.current.textContent = `${prefix}${obj.count.toLocaleString()}${suffix}`;
          }
        }
      });
    });

    // Cleanup
    return () => {
      // anime.js no tiene pause, simplemente dejamos que termine
    };
  }, [value, duration, delay, prefix, suffix]);

  return <span ref={counterRef}>0</span>;
};

AnimatedCounter.propTypes = {
  value: PropTypes.number.isRequired,
  duration: PropTypes.number,
  delay: PropTypes.number,
  prefix: PropTypes.string,
  suffix: PropTypes.string,
};

export default AnimatedCounter;

import React, { useEffect, useRef } from 'react';
import PropTypes from 'prop-types';
import { Box } from '@mui/material';

const AnimatedLogo = ({ size = 200, autoPlay = true, loop = false }) => {
  const svgRef = useRef(null);

  useEffect(() => {
    if (!autoPlay || !svgRef.current) return;

    // Importar anime din谩micamente
    import('animejs').then((animeModule) => {
      // Intentar obtener la funci贸n anime de diferentes formas
      let animeFunc = animeModule.default;
      if (!animeFunc && typeof animeModule === 'function') {
        animeFunc = animeModule;
      }
      if (!animeFunc && animeModule.anime) {
        animeFunc = animeModule.anime;
      }

      // Si a煤n no tenemos la funci贸n, buscar en las propiedades del m贸dulo
      if (!animeFunc) {
        const keys = Object.keys(animeModule);
        for (const key of keys) {
          if (typeof animeModule[key] === 'function') {
            animeFunc = animeModule[key];
            break;
          }
        }
      }

      if (!animeFunc) {
        console.error('No se pudo cargar anime.js', animeModule);
        return;
      }

      const paths = svgRef.current.querySelectorAll('path');

      // Animaci贸n de trazado (stroke)
      animeFunc({
        targets: paths,
        strokeDashoffset: [animeFunc.setDashoffset, 0],
        easing: 'easeInOutSine',
        duration: 2000,
        delay: function(el, i) { return i * 250 },
        direction: 'alternate',
        loop: loop,
        complete: function() {
          // Despu茅s del trazado, animar el fill
          animeFunc({
            targets: paths,
            fill: ['rgba(233, 30, 99, 0)', 'rgba(233, 30, 99, 1)'],
            duration: 1000,
            easing: 'easeInOutQuad'
          });
        }
      });

      // Animaci贸n de escala pulsante
      animeFunc({
        targets: svgRef.current,
        scale: [0.95, 1],
        duration: 2000,
        easing: 'easeInOutSine',
        loop: true,
        direction: 'alternate',
        delay: 2500
      });
    });
  }, [autoPlay, loop]);

  return (
    <Box
      sx={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        width: size,
        height: size,
      }}
    >
      <svg
        ref={svgRef}
        width={size}
        height={size}
        viewBox="0 0 200 200"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
      >
        {/* Logo "E" estilizado para Empoderar */}
        <path
          d="M50 50 L150 50 M50 50 L50 150 M50 100 L130 100 M50 150 L150 150"
          stroke="#E91E63"
          strokeWidth="8"
          strokeLinecap="round"
          strokeLinejoin="round"
          fill="none"
        />

        {/* C铆rculo decorativo */}
        <circle
          cx="100"
          cy="100"
          r="80"
          stroke="#9C27B0"
          strokeWidth="4"
          fill="none"
          opacity="0.3"
        />

        {/* Estrella de empoderamiento */}
        <path
          d="M100 40 L110 70 L140 70 L115 90 L125 120 L100 100 L75 120 L85 90 L60 70 L90 70 Z"
          fill="rgba(233, 30, 99, 0)"
          stroke="#E91E63"
          strokeWidth="2"
        />
      </svg>
    </Box>
  );
};

AnimatedLogo.propTypes = {
  size: PropTypes.number,
  autoPlay: PropTypes.bool,
  loop: PropTypes.bool,
};

export default AnimatedLogo;

import React, { useEffect, useRef } from 'react';
import PropTypes from 'prop-types';
import { LinearProgress, Box } from '@mui/material';

const AnimatedProgressBar = ({ value, duration = 1500, delay = 0, color = 'primary', height = 8 }) => {
  const progressRef = useRef(null);
  const animationRef = useRef(null);

  useEffect(() => {
    if (!progressRef.current) return;

    // Importar anime din谩micamente
    import('animejs').then((animeModule) => {
      // Intentar obtener la funci贸n anime de diferentes formas
      let animeFunc = animeModule.default;
      if (!animeFunc && typeof animeModule === 'function') {
        animeFunc = animeModule;
      }
      if (!animeFunc && animeModule.anime) {
        animeFunc = animeModule.anime;
      }

      // Si a煤n no tenemos la funci贸n, buscar en las propiedades del m贸dulo
      if (!animeFunc) {
        const keys = Object.keys(animeModule);
        for (const key of keys) {
          if (typeof animeModule[key] === 'function') {
            animeFunc = animeModule[key];
            break;
          }
        }
      }

      if (!animeFunc) {
        console.error('No se pudo cargar anime.js', animeModule);
        return;
      }

      // Objeto para animar el progreso
      const obj = { progress: 0 };

      // Crear nueva animaci贸n
      animationRef.current = animeFunc({
        targets: obj,
        progress: value,
        duration: duration,
        delay: delay,
        easing: 'easeOutCubic',
        update: function() {
          if (progressRef.current) {
            // Actualizar el progreso de la barra
            const progressBar = progressRef.current.querySelector('.MuiLinearProgress-bar');
            if (progressBar) {
              progressBar.style.transform = `translateX(-${100 - obj.progress}%)`;
            }
          }
        }
      });
    });

    // Cleanup
    return () => {
      // anime.js no tiene pause, simplemente dejamos que termine
    };
  }, [value, duration, delay]);

  return (
    <Box ref={progressRef} sx={{ width: '100%' }}>
      <LinearProgress
        variant="determinate"
        value={0}
        color={color}
        sx={{
          height: height,
          borderRadius: height / 2,
          backgroundColor: 'rgba(0, 0, 0, 0.08)',
          '& .MuiLinearProgress-bar': {
            borderRadius: height / 2,
            transition: 'none', // Desactivar transici贸n CSS para usar anime.js
          }
        }}
      />
    </Box>
  );
};

AnimatedProgressBar.propTypes = {
  value: PropTypes.number.isRequired,
  duration: PropTypes.number,
  delay: PropTypes.number,
  color: PropTypes.string,
  height: PropTypes.number,
};

export default AnimatedProgressBar;

import React, { useEffect, useRef } from 'react';
import PropTypes from 'prop-types';
import { Box, Typography } from '@mui/material';

const LoadingAnimation = ({ type = 'dots', size = 60, color = 'primary', text = '' }) => {
  const containerRef = useRef(null);

  useEffect(() => {
    if (!containerRef.current) return;

    // Importar anime din谩micamente
    import('animejs').then((animeModule) => {
      // Intentar obtener la funci贸n anime de diferentes formas
      let animeFunc = animeModule.default;
      if (!animeFunc && typeof animeModule === 'function') {
        animeFunc = animeModule;
      }
      if (!animeFunc && animeModule.anime) {
        animeFunc = animeModule.anime;
      }

      // Si a煤n no tenemos la funci贸n, buscar en las propiedades del m贸dulo
      if (!animeFunc) {
        const keys = Object.keys(animeModule);
        for (const key of keys) {
          if (typeof animeModule[key] === 'function') {
            animeFunc = animeModule[key];
            break;
          }
        }
      }

      if (!animeFunc) {
        console.error('No se pudo cargar anime.js', animeModule);
        return;
      }

      const elements = containerRef.current.querySelectorAll('.loading-element');

      if (type === 'dots') {
        // Animaci贸n de puntos saltando
        animeFunc({
          targets: elements,
          translateY: [0, -20, 0],
          duration: 800,
          delay: (el, i) => i * 100,
          loop: true,
          easing: 'easeInOutQuad'
        });
      } else if (type === 'pulse') {
        // Animaci贸n de pulso
        animeFunc({
          targets: elements,
          scale: [1, 1.5, 1],
          opacity: [1, 0.5, 1],
          duration: 1200,
          delay: (el, i) => i * 150,
          loop: true,
          easing: 'easeInOutSine'
        });
      } else if (type === 'rotate') {
        // Animaci贸n de rotaci贸n
        animeFunc({
          targets: containerRef.current.querySelector('.loading-spinner'),
          rotate: '360deg',
          duration: 1500,
          loop: true,
          easing: 'linear'
        });
      } else if (type === 'wave') {
        // Animaci贸n de onda
        animeFunc({
          targets: elements,
          scaleY: [1, 2, 1],
          duration: 1000,
          delay: (el, i) => i * 100,
          loop: true,
          easing: 'easeInOutQuad'
        });
      }
    });
  }, [type]);

  const getColorValue = () => {
    const colors = {
      primary: '#E91E63',
      secondary: '#9C27B0',
      success: '#4CAF50',
      error: '#F44336',
      warning: '#FF9800',
      info: '#2196F3',
    };
    return colors[color] || colors.primary;
  };

  const renderLoading = () => {
    const colorValue = getColorValue();
    const dotSize = size / 5;

    if (type === 'dots') {
      return (
        <Box sx={{ display: 'flex', gap: 1, alignItems: 'center' }}>
          {[1, 2, 3].map((i) => (
            <Box
              key={i}
              className="loading-element"
              sx={{
                width: dotSize,
                height: dotSize,
                borderRadius: '50%',
                bgcolor: colorValue,
              }}
            />
          ))}
        </Box>
      );
    } else if (type === 'pulse') {
      return (
        <Box sx={{ display: 'flex', gap: 1 }}>
          {[1, 2, 3, 4].map((i) => (
            <Box
              key={i}
              className="loading-element"
              sx={{
                width: dotSize,
                height: dotSize,
                borderRadius: '50%',
                bgcolor: colorValue,
              }}
            />
          ))}
        </Box>
      );
    } else if (type === 'rotate') {
      return (
        <Box
          className="loading-spinner"
          sx={{
            width: size,
            height: size,
            border: `${size / 10}px solid rgba(0,0,0,0.1)`,
            borderTop: `${size / 10}px solid ${colorValue}`,
            borderRadius: '50%',
          }}
        />
      );
    } else if (type === 'wave') {
      return (
        <Box sx={{ display: 'flex', gap: 0.5, alignItems: 'flex-end', height: size }}>
          {[1, 2, 3, 4, 5].map((i) => (
            <Box
              key={i}
              className="loading-element"
              sx={{
                width: dotSize / 2,
                height: size / 3,
                bgcolor: colorValue,
                borderRadius: 1,
              }}
            />
          ))}
        </Box>
      );
    }
  };

  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        gap: 2,
      }}
    >
      <Box ref={containerRef}>{renderLoading()}</Box>
      {text && (
        <Typography variant="body2" color="text.secondary">
          {text}
        </Typography>
      )}
    </Box>
  );
};

LoadingAnimation.propTypes = {
  type: PropTypes.oneOf(['dots', 'pulse', 'rotate', 'wave']),
  size: PropTypes.number,
  color: PropTypes.oneOf(['primary', 'secondary', 'success', 'error', 'warning', 'info']),
  text: PropTypes.string,
};

export default LoadingAnimation;

import React from 'react';
import { Box, CircularProgress, Typography } from '@mui/material';
import { motion } from 'framer-motion';

const LoadingScreen = () => {
  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        minHeight: '100vh',
        backgroundColor: 'background.default',
        gap: 3
      }}
    >
      <motion.div
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <CircularProgress 
          size={60} 
          thickness={4}
          sx={{ color: 'primary.main' }}
        />
      </motion.div>
      
      <motion.div
        initial={{ y: 20, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ delay: 0.2, duration: 0.5 }}
      >
        <Typography 
          variant="h6" 
          color="text.secondary"
          sx={{ fontWeight: 500 }}
        >
          Cargando...
        </Typography>
      </motion.div>
    </Box>
  );
};

export default LoadingScreen;

import React from 'react';
import { Box, Card, CardContent } from '@mui/material';
import { motion } from 'framer-motion';
import PropTypes from 'prop-types';

// Skeleton b谩sico con animaci贸n de pulso
export const Skeleton = ({ width = '100%', height = 20, borderRadius = 4, sx = {} }) => (
  <motion.div
    animate={{ opacity: [0.4, 0.8, 0.4] }}
    transition={{ 
      repeat: Infinity, 
      duration: 1.5,
      ease: "easeInOut"
    }}
    style={{
      width,
