                    <MenuItem value="finanzas">Finanzas</MenuItem>
                    <MenuItem value="costura">Costura</MenuItem>
                    <MenuItem value="cocina">Cocina</MenuItem>
                    <MenuItem value="tecnologia">Tecnolog铆a</MenuItem>
                    <MenuItem value="liderazgo">Liderazgo</MenuItem>
                    <MenuItem value="otros">Otros</MenuItem>
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} md={4}>
                <FormControl fullWidth>
                  <InputLabel>Nivel</InputLabel>
                  <Select
                    value={levelFilter}
                    onChange={(e) => setLevelFilter(e.target.value)}
                    label="Nivel"
                  >
                    <MenuItem value="">Todos los niveles</MenuItem>
                    <MenuItem value="basico">B谩sico</MenuItem>
                    <MenuItem value="intermedio">Intermedio</MenuItem>
                    <MenuItem value="avanzado">Avanzado</MenuItem>
                  </Select>
                </FormControl>
              </Grid>
            </Grid>
          </CardContent>
        </Card>
      </motion.div>

      {/* Lista de cursos */}
      {coursesLoading ? (
        <Box sx={{ textAlign: 'center', py: 8 }}>
          <LinearProgress sx={{ width: '50%', mx: 'auto' }} />
          <Typography variant="body1" sx={{ mt: 2 }}>
            Cargando cursos...
          </Typography>
        </Box>
      ) : (
        <Grid container spacing={3}>
          {filteredCourses.map((course, index) => (
            <Grid item xs={12} sm={6} md={4} key={course.id}>
              <CourseCard
                course={course}
                isEnrolled={isEnrolled(course.id)}
                enrollment={getEnrollment(course.id)}
                onEnroll={handleEnroll}
                t={t}
              />
            </Grid>
          ))}
        </Grid>
      )}

      {/* Mensaje si no hay cursos */}
      {!coursesLoading && filteredCourses.length === 0 && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <Card sx={{ textAlign: 'center', py: 8 }}>
            <School sx={{ fontSize: 64, color: 'text.secondary', mb: 2 }} />
            <Typography variant="h6" color="text.secondary" sx={{ mb: 1 }}>
              No se encontraron cursos
            </Typography>
            <Typography variant="body2" color="text.secondary">
              Intenta ajustar los filtros de b煤squeda
            </Typography>
          </Card>
        </motion.div>
      )}
    </Box>
  );
};

